Content.Code:
showN=maxN;
for(int i=0; i<maxN;i++) {
  colors[i]=i;
}
vis[0]=true;
coef=powCoef;
analysisTitle="Power Spectrum";

n = Math.max(2,n);  // at least two data points to compute coefficients
((FFTReal)fft).setN(n);
// number of samples may have changed so reallocate
y = new double[n];
fftCoef = new double[n];
sinCoef = new double[n/2];
cosCoef = new double[n/2];
powCoef = new double[n/2];
freqs = new double[n/2];

EventContent.ZeroCondition:
double err=Double.POSITIVE_INFINITY;
for(int i=0; i<showN; i++) {
  PoincareIndex=i;
  err= M-w*t;
}
return err;

double err=Double.MAX_VALUE;
for(int i=0; i<showN;i++){
  if(omega[i]<0 && (Math.PI+AngularDisplacement[i])<err){
    err=Math.PI+AngularDisplacement[i];
    eventIndex=i;
  }
}
return err;

double err=Double.MAX_VALUE;
for(int i=0; i<showN;i++){
  if(omega[i]>0 && (Math.PI-AngularDisplacement[i])<err){
    err=Math.PI-AngularDisplacement[i];
    eventIndex=i;
  }
}
return err;

double err=Double.POSITIVE_INFINITY;
for(int i=0; i<showN; i++) {
  FourierIndex=i;
  err= t-(T);
}
return err;

EventContent.Action:
T+=dt;//Takes one data point per step
freqs[Num]=x[FourierIndex];
y[Num]=AngularDisplacement[FourierIndex];
_view.trail.addPoint(Num,y[Num]);
_println("x="+Num+"y="+y[Num]);
Num+=1;
x[FourierIndex]+=1;
if (Num==q-1){
  computeFFT();
  _pause();
}

Content.Code:
//Evolutions for Fourier Transformation
y[Num]=AngularDisplacement[0];
freqs[Num+1]=freqs[Num]+1;
_view.trail.addPoint(freqs[Num],y[Num]);
_println("x="+freqs[Num]+"y="+y[Num]);
Num++;
FFTi++;
if (FFTi==q-4) {
  computeFFT();
  FFTi=0;
}

//Calculate Relevant Coefficients
for(int i=0; i<showN;i++) {
  theta[i]=AngularDisplacement[i];
  xBob[i]=L*Math.sin(AngularDisplacement[i]);  // position hot spot under the bob
  yBob[i]=-L*Math.cos(AngularDisplacement[i])+Y;
  distance=Math.sqrt(xBob[i]*xBob[i]+yBob[i]*yBob[i]);
  LeftParameter=Math.acos((2*g*L)/(d0*d0*w*w));
  RightParameter=(2*Math.PI-Math.acos(2*g*L/(d0*d0*w*w)));
  U[i]=(g/L)*Math.cos(AngularDisplacement[i])+0.25*0.01*L*w*w*Math.sin(AngularDisplacement[i])*Math.sin(AngularDisplacement[i]);
}

public void dragPendulum () {
  org.opensourcephysics.drawing2d.ElementTrail trail;
  trail = (org.opensourcephysics.drawing2d.ElementTrail) _view.phaseSpaceTrailSet.getElement(dragIndex);
  trail.addPoint(AngularDisplacement[dragIndex],omega[dragIndex]);
  AngularDisplacement[dragIndex] = Math.atan2(xBob[dragIndex],-yBob[dragIndex]);  // use the hot spot to find the angle
  omega[dragIndex] = 0.0;
  trail.moveToPoint(AngularDisplacement[dragIndex],omega[dragIndex]);
  _view.Ueff_Trail.clear();
}

public void selectMode() {
  for(int i=0; i<showN;i++) {
    _view.clearData();
    if(initialCondition.equals("1st Period Doubling")) {
      AngularDisplacement[i]=2.542;
      d0=0.115;
      w=4.6;
      b=0;
      g=-9.8;
      Num=0;
      FFTi=0;
      FourierMax=300;
    } else if(initialCondition.equals("Chaotic")) {
      AngularDisplacement[i]=1.8;
      d0=0.25;
      w=65;
      Num=0;
      FFTi=0;
      FourierMax=600;
    } else {
      energyAction();
    }
  }
}

public void energyAction () {
  for(int i=0; i<showN;i++) {
    AngularDisplacement[i]=0;
    AngularDisplacement[i]=Math.PI;
    omega[i]=0;
    t=0;
    _view.clearData();
    Num=0;
  }
}

public void poincarePanelAction() {
  for(int i=0; i<showN;i++) {
    double x =_view.poincarePlottingPanel.getMouseX();
    double y =_view.poincarePlottingPanel.getMouseY();
    if(t!=0) {
      colorIndex++; // change the color only if data has already been collected.
    }
    AngularDisplacement[i]=x;
    omega[i]=0;
    t=0;
    clearTrails();
    Y=0;
    _view.PoincareTrail.addPoint(AngularDisplacement[i],omega[i]);
    _view.dataRaster.append(colorIndex,AngularDisplacement[i],omega[i]);
  }
}

public void computeFFT () {
  System.arraycopy(y,0,fftCoef,0,n);
  ((FFTReal)fft).transform(fftCoef);
  for(int i=0, len=sinCoef.length; i<len; i++){
    cosCoef[i]=2*fftCoef[2*i]/(n/2);
    sinCoef[i]=-2*fftCoef[2*i+1]/(n/2);
    powCoef[i]=sinCoef[i]*sinCoef[i]+cosCoef[i]*cosCoef[i];
  }
}

public void set_sinCoefficients() {
  analysisTitle="Sine Coefficients";
  coef=sinCoef;
}

public void set_cosCoefficients() {
  analysisTitle="Cosine Coefficients";
  coef=cosCoef;
}

public void set_powCoefficients() {
  analysisTitle="Power Spectrum";
  coef=powCoef;
}
